// components/StudentPortal.js
import React, { useState, useEffect } from 'react';
import { useWeb3 } from '../contexts/Web3Context';
import api from '../services/api';
import LoadingSpinner from './LoadingSpinner';

const StudentPortal = () => {
  const [studentId, setStudentId] = useState('');
  const [certificates, setCertificates] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [walletCertificates, setWalletCertificates] = useState([]);
  
  const { account, connectWallet, isConnected } = useWeb3();

  useEffect(() => {
    if (isConnected && account) {
      fetchWalletCertificates();
    }
  }, [account, isConnected]);

  const fetchWalletCertificates = async () => {
    // This would require querying the blockchain for NFTs owned by the connected wallet
    // For now, we'll show a placeholder
    console.log('Fetching certificates for wallet:', account);
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    
    if (!studentId.trim()) {
      setError('Please enter a student ID');
      return;
    }

    try {
      setLoading(true);
      setError(null);
      
      const response = await api.get(`/certificates/student/${studentId}`);
      setCertificates(response.data.certificates);
      
      if (response.data.certificates.length === 0) {
        setError('No certificates found for this student ID');
      }
    } catch (error) {
      console.error('Search error:', error);
      setError(error.response?.data?.error || 'Failed to search certificates');
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleDateString('en-UG', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div className="student-portal">
      <div className="portal-header">
        <h1>Student Certificate Portal</h1>
        <p>Access and manage your academic certificates</p>
      </div>

      <div className="portal-content">
        {/* Wallet Connection Section */}
        <div className="wallet-section">
          <h2>Connect Your Wallet</h2>
          <p>Connect your digital wallet to view certificates you own as NFTs</p>
          
          {!isConnected ? (
            <button onClick={connectWallet} className="btn btn-primary">
              Connect Wallet
            </button>
          ) : (
            <div className="wallet-connected">
              <div className="wallet-info">
                <span className="wallet-icon">üîó</span>
                <div>
                  <p><strong>Wallet Connected</strong></p>
                  <p className="wallet-address">
                    {account.slice(0, 6)}...{account.slice(-4)}
                  </p>
                </div>
              </div>
              
              {walletCertificates.length > 0 ? (
                <div className="wallet-certificates">
                  <h3>Your Certificate NFTs</h3>
                  {/* Wallet certificates would be displayed here */}
                </div>
              ) : (
                <div className="no-wallet-certificates">
                  <p>No certificate NFTs found in your wallet</p>
                  <small>Make sure you're using the wallet address provided to your institution</small>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Student ID Search Section */}
        <div className="search-section">
          <h2>Search by Student ID</h2>
          <p>Enter your student ID to find certificates issued to you</p>
          
          <form onSubmit={handleSearch} className="search-form">
            <div className="form-group">
              <label htmlFor="studentId">Student ID</label>
              <div className="input-group">
                <input
                  type="text"
                  id="studentId"
                  value={studentId}
                  onChange={(e) => setStudentId(e.target.value)}
                  placeholder="Enter your student ID"
                />
                <button type="submit" disabled={loading} className="btn btn-primary">
                  {loading ? 'Searching...' : 'Search'}
                </button>
              </div>
            </div>
          </form>

          {error && (
            <div className="alert alert-error">
              {error}
            </div>
          )}

          {loading && <LoadingSpinner message="Searching for certificates..." />}

          {certificates.length > 0 && (
            <div className="certificates-results">
              <h3>Your Certificates ({certificates.length})</h3>
              <div className="certificates-grid">
                {certificates.map(cert => (
                  <div key={cert._id} className="certificate-card">
                    <div className="certificate-header">
                      <h4>{cert.courseName}</h4>
                      <span className={`status ${cert.isRevoked ? 'revoked' : 'active'}`}>
                        {cert.isRevoked ? 'Revoked' : 'Active'}
                      </span>
                    </div>
                    
                    <div className="certificate-body">
                      <p><strong>Institution:</strong> {cert.institutionId?.name}</p>
                      <p><strong>Type:</strong> {cert.certificateType}</p>
                      <p><strong>Grade:</strong> {cert.grade}</p>
                      <p><strong>Issue Date:</strong> {formatDate(cert.issueDate)}</p>
                      <p><strong>Graduation:</strong> {formatDate(cert.graduationDate)}</p>
                    </div>
                    
                    <div className="certificate-footer">
                      <span className="token-id">Token ID: #{cert.tokenId}</span>
                      <div className="certificate-actions">
                        <a 
                          href={`/verify/${cert.tokenId}`}
                          className="btn btn-outline btn-sm"
                          target="_blank"
                          rel="noopener noreferrer"
                        >
                          Verify
                        </a>
                        {cert.ipfsHash && (
                          <a 
                            href={`https://gateway.pinata.cloud/ipfs/${cert.ipfsHash}`}
                            className="btn btn-outline btn-sm"
                            target="_blank"
                            rel="noopener noreferrer"
                          >
                            View Document
                          </a>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* Information Section */}
        <div className="info-section">
          <h2>About Digital Certificates</h2>
          <div className="info-grid">
            <div className="info-card">
              <div className="info-icon">üîê</div>
              <h3>Secure & Tamper-Proof</h3>
              <p>Your certificates are stored on the blockchain, making them impossible to forge or alter.</p>
            </div>
            
            <div className="info-card">
              <div className="info-icon">üë§</div>
              <h3>You Own Your Credentials</h3>
              <p>Certificates are minted as NFTs directly to your wallet, giving you full ownership.</p>
            </div>
            
            <div className="info-card">
              <div className="info-icon">üåç</div>
              <h3>Globally Accessible</h3>
              <p>Access your certificates from anywhere in the world, anytime.</p>
            </div>
            
            <div className="info-card">
              <div className="info-icon">‚ö°</div>
              <h3>Instant Verification</h3>
              <p>Employers and institutions can verify your certificates in seconds.</p>
            </div>
          </div>
        </div>

        {/* Wallet Setup Guide */}
        <div className="guide-section">
          <h2>Setting Up Your Digital Wallet</h2>
          <div className="guide-steps">
            <div className="step">
              <div className="step-number">1</div>
              <div className="step-content">
                <h4>Install MetaMask</h4>
                <p>Download and install the MetaMask browser extension or mobile app</p>
              </div>
            </div>
            
            <div className="step">
              <div className="step-number">2</div>
              <div className="step-content">
                <h4>Create Wallet</h4>
                <p>Set up a new wallet and securely store your seed phrase</p>
              </div>
            </div>
            
            <div className="step">
              <div className="step-number">3</div>
              <div className="step-content">
                <h4>Add Base Network</h4>
                <p>Configure MetaMask to connect to the Base blockchain network</p>
              </div>
            </div>
            
            <div className="step">
              <div className="step-number">4</div>
              <div className="step-content">
                <h4>Share Address</h4>
                <p>Provide your wallet address to your institution for certificate issuance</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
export default Login;
